import './Main.css';

const Main = () => {
  return (
    <section className="hero">
      <div className="hero-content">
        <h1 className="hero-title">Learn New Skills & Earn Certifications</h1>
        <p className="hero-subtext">
          Learn anything relating to farming with technology
        </p>

        <div className="search-bar">
          <input type="text" placeholder="What do you want to learn" />
          <button className="search-button">Search</button>
        </div>

        <p className="people-search-for">People search for</p>
        <div className="search-tags">
          <button className="tag">Soil Health</button>
          <button className="tag">Agric Food System</button>
          <button className="tag">Permaculture</button>
        </div>

        <div className="cta-buttons">
          <button className="primary-btn">Start Learning Now</button>
          <a href="#" className="secondary-btn">Explore Courses â†’</a>
        </div>
      </div>
    </section>
  );
};

export default Hero;
