import React from "react";
import "./Stats.css"; // Import the CSS file

const Stats = () => {
  return (
    <section className="stats-section">
      <div className="stat">
        <img src="/assets/courses-icon.png" alt="Courses Icon" className="icon" />
        <div>
          <h2>500+</h2>
          <p>Total Courses</p>
        </div>
      </div>

      <div className="stat">
        <img src="/assets/students-icon.png" alt="Students Icon" className="icon" />
        <div>
          <h2>1000+</h2>
          <p>Students Globally</p>
        </div>
      </div>
    </section>
  );
};

export default Stats;
